# Send email function with error handling
def send_email(to_email, name):
    try:
        msg = MIMEMultipart()
        msg['From'] = sender_email
        msg['To'] = to_email
        msg['Subject'] = 'Important Update from neuron.ai'

        body = create_email_body(name)
        msg.attach(MIMEText(body, 'html'))

        with smtplib.SMTP('smtp.gmail.com', 587) as server:
            server.starttls()
            server.login(sender_email, sender_password)
            server.send_message(msg)
        print(f'Email sent to {name} at {to_email}')
    except Exception as e:
        print(f'Failed to send email to {name} at {to_email}. Error: {e}')

# Sending emails
